#define INDEX_HTML (F("<!doctype html><meta charset=UTF-8><title>CO2 monitor</title><canvas id=chart></canvas><script src=https://cdn.jsdelivr.net/npm/chart.js></script><script>const chart=new Chart(document.getElementById(\"chart\"),{type:\"line\",data:{labels:[],datasets:[{data:[],label:\"CO2(ppm)\",borderColor:\"#FF5F47\",yAxisID:\"y\"},{data:[],label:\"Temperature(Celsius)\",borderColor:\"#9CA4FF\",yAxisID:\"y1\"}]},options:{responsive:!0,interaction:{mode:\"index\",intersect:!1},plugins:{title:{display:!0,text:\"CO2 Monitor\"}},scales:{y:{type:\"linear\",position:\"left\",suggestedMin:400,suggestedMax:1e3},y1:{type:\"linear\",position:\"right\",grid:{drawOnChartArea:!1}}}}});async function fetchDataAndDrawGraph(){try{const t=await(await fetch(\"/histories\")).json(),a=[];for(let e=0;e<t.times.length;e++)a.push(new Date(Date.now()-t.times[e]).toLocaleTimeString());chart.data.labels=a,chart.data.datasets[0].data=t.co2Logs,chart.data.datasets[1].data=t.temperatureLogs,chart.update()}catch(t){console.log(\"Error cannot get historiesdata\",t)}}fetchDataAndDrawGraph(),setInterval(fetchDataAndDrawGraph,5e3)</script>"))